import React from "react";
import { MDBDataTable } from "mdbreact";
import { Col, Row, Container, Button } from "react-bootstrap";
import ShowAddModal from "./AddRoomModal";
import ShowEditModal from "./EditRoomModal";
import Axios from "axios";

class RoomManager extends React.Component {
  state = {
    columns: [],
    rows: [],
  };
  componentDidMount() {
    this.retrieveEvents();
  }
  retrieveEvents = () => {
    Axios.get("http://ec2-18-224-228-195.us-east-2.compute.amazonaws.com:3001/api/rooms")
      .then((response) => {
        let dataColumns = [];
        Object.keys(response.data[0]).map((item) => {
          let newColumnEntry = {
            label: item,
            field: item,
            sort: "asc",
          };
          return dataColumns.push(newColumnEntry);
        });
        dataColumns.push({ label: "Action", field: "editField", sort: "asc" })
        let newRes = []
        response.data.map(item => {
          let newObj = {
            n: item.Name,
            s: item.Status,
            i: item._id
          }
          item.editField = <Button onClick={() => this.handleClick(newObj)} size="sm" variant="light">Edit</Button>
          return newRes.push(item)
        })
        this.setState({ columns: dataColumns, rows: [...newRes] });
      })
      .catch((error) => console.log(error));
  };
  handleClick = (data) => {
    console.log("edit button clicked")
    return <ShowEditModal selectedRoom={data} open={true}></ShowEditModal>;
  }
  render() {
    return (
      <>
        <Container>
          <Col>
            <Row className="justify-content-md-center pt-4">
              <h1>Room Manager</h1>
            </Row>
            <Row className="justify-content-md-center">
              <MDBDataTable
                btn
                striped
                small
                bordered
                autoWidth
                responsive
                entries={8}
                dark
                tbodyTextWhite
                theadTextWhite
                displayEntries={false}
                paging={true}
                data={this.state}
                noBottomColumns
              />
            </Row>
            <Row className="justify-content-md-center pt-5 flex" sm={6}>
              <ShowAddModal state={this.state}></ShowAddModal>
            </Row>
          </Col>
        </Container>
      </>
    );
  }
}

export default RoomManager;
