import React from "react";
import { MDBDataTable } from "mdbreact";
import { Col, Row, Container } from "react-bootstrap";
import Axios from "axios";

class EventHistory extends React.Component {
  state = {
    columns: [],
    rows: [],
  };
  componentDidMount() {
    this.retrieveEvents();
  }

  retrieveEvents = () => {
    Axios.get("http://ec2-18-224-228-195.us-east-2.compute.amazonaws.com:3001/api/events")
      .then((response) => {
        let dataColumns = [];
        Object.keys(response.data[3]).map((item) => {
          let newColumnEntry = {
            label: item,
            field: item,
            sort: "asc",
          };
          return dataColumns.push(newColumnEntry);
        });
        this.setState({ columns: dataColumns, rows: [...response.data] });
      })
      .catch((error) => console.log(error));
  };
  render() {
    return (
      <>
        <Container>
          <Col>
            <Row className="justify-content-md-center pt-4">
              <h1>Event History</h1>
            </Row>
            <Row className="justify-content-md-center">
              <MDBDataTable
                striped
                small
                bordered
                responsive
                entries={8}
                dark
                tbodyTextWhite
                theadTextWhite
                displayEntries={false}
                paging={true}
                data={this.state}
                noBottomColumns
                sortable
              />
            </Row>
          </Col>
        </Container>
      </>
    );
  }
}

export default EventHistory;
