import React from "react";
import { ListGroup } from "react-bootstrap";
import Axios from "axios";
export default class ChatRoom extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      chatRoomList: []
    };
  }
  componentDidMount() {
    this.retrieveRoomList();
  }

  retrieveRoomList = () => {
    Axios.get("http://ec2-18-224-228-195.us-east-2.compute.amazonaws.com:3001/api/rooms")
      .then((response) => {
        let rooms = [...this.state.chatRoomList];
        (response.data).map(val => {
          return rooms.push(val.Name);
        })
        this.setState({ chatRoomList: rooms })
      })
      .catch((error) => {
        console.log(error);
      });
  };

  setRoom = event => {
    this.props.onRoomSelect(event.target.value);
  };

  render() {
    return (
      <div>
        <ListGroup className="roomList">
          <ListGroup.Item disabled>
            <b>Room List</b>
          </ListGroup.Item>
          {this.state.chatRoomList.map((val, ctr) => {
            return (
              <React.Fragment key={ctr}>
                <ListGroup.Item action onClick={this.setRoom} value={val}>
                  {val}
                </ListGroup.Item>
              </React.Fragment>
            );
          })}
        </ListGroup>
      </div>
    );
  }
}
